import React, { useState } from 'react';
import{ChannelList, useChatContext} from 'stream-chat-react';
import{ChannelSearch, TeamChannelList, TeamChannelPreview} from './';
import Cookies from 'universal-cookie';
import icon1 from '../assets/icon.png';
import icon2 from '../assets/icon2.jpg';
import { initialState } from 'stream-chat-react/dist/components/Channel/channelState';

const cookies = new Cookies();

const SideBar = () => (
    <div className = "channel-list_sidebar">
        <div className = "channel-list_sidebar_icon1">
            <div className = "icon1_inner">
                <img src = {icon1} alt = "icon1" width = "30"></img>
            </div>
        </div>
        <div className = "channel-list_sidebar_icon2">
            <div className = "icon2_inner">
                <img src = {icon2} alt = "icon2" width = "30"></img>
            </div>
        </div>
    </div>
)

const CompanyHeader = () => (
    <div className = "channel-list_header">
        <p className = "channel-list_header_text">
            Chat Program
        </p>
    </div>
)

const customChannelTeamFilter = (channels) => {
    return channels.filter((channel) => channel.type === "team");
}
const customChannelMessageingFilter = (channels) => {
    return channels.filter((channel) => channel.type === "messaging");
}

const ChannelListContent = ({isCreating, setIsCreating, setCreateType, setIsEditing}) => {
    const {client} = useChatContext();
    const logout = () => {
        cookies.remove("token");
        cookies.reomve("userId");
        cookies.remove("username");
        cookies.remove("hashedPassword");
    }
    const filters = {members : {$in: [client.userID]}};
    return (
    <>
        <SideBar></SideBar>
        <div className = "channel-list_list_wrapper">
            <CompanyHeader></CompanyHeader>
            <ChannelSearch></ChannelSearch>
            <ChannelList 
                filters = {filters}
                channelRenderFilterFn = {customChannelTeamFilter}
                List = {(listProps) => (
                    <TeamChannelList 
                        {...listProps}
                        type = 'team'
                        isCreating = {isCreating}
                        setIsCreating = {setIsCreating}
                        setCreateType = {setCreateType}
                        setIsEditing = {setIsEditing}
                    >
                    </TeamChannelList>)}
                    Preview = {(previewProps) => (
                        <TeamChannelPreview
                            {...previewProps}
                            type = 'team'
                            >
                        </TeamChannelPreview>
                    )} 
            ></ChannelList>
            <ChannelList 
                filters = {filters}
                channelRenderFilterFn = {customChannelMessageingFilter}
                List = {(listProps) => (
                    <TeamChannelList 
                        {...listProps}
                        type = 'messaging'
                        isCreating = {isCreating}
                        setIsCreating = {setIsCreating}
                        setCreateType = {setCreateType}
                        setIsEditing = {setIsEditing}
                    >
                    </TeamChannelList>)}
                    Preview = {(previewProps) => (
                        <TeamChannelPreview
                            {...previewProps}
                            type = 'messaging'
                            >
                        </TeamChannelPreview>
                    )} 
            ></ChannelList>
        </div>
    </>
  );
}

const ChannelListContainer = ([setCreateType, setIsCreating, setIsEditing]) => {
    return (
        <>
            <div className = 'channel-list_container'>
                <ChannelListContent 
                    setIsCreating = {setIsCreating}
                    setCreateType = {setCreateType}
                    setIsEditing = {setIsEditing}
                />
            </div>
        </>
    )
}

export default ChannelListContainer
