import React, { useState } from "react";
import { MessageList, MessageInput, Thread, Window, useChannelActionContext, Avatar, useChannelStateContext, useChatContext } from "stream-chat-react";

import {ChannelInfo} from "../assets/ChannelInfo.js";

export const GiphyContext = React.createContext({});

const ChannelInner = ({ setIsEditing }) => {
  const [giphyState, setGiphyState] = useState(false);
  const {sendMessage} = useChannelActionContext();
  
  const overrideSubmitHandler = (message) => {
    let updatedMessage = {
      attachments: message.attachments,
      mentioned_users: message.mentioned_users,
      parent_id: message.parent?.id,
      parent: message.parent,
      text: message.text
    };
    
    if (giphyState) {
      updatedMessage = {...updatedMessage, text: `/giphy ${message.text}`};
    }
    
    if (sendMessage) {
      sendMessage(updatedMessage);
      setGiphyState(false);
    }
  };

  return (
    <GiphyContext.Provider value = {{ giphyState, setGiphyState }}>
      <div style = {{display: "flex", width: "100%"}}>
        <Window>
          <TeamChannelHeader setIsEditing = {setIsEditing} />
          <MessageList />
          <MessageInput overrideSubmitHandler = {overrideSubmitHandler} />
        </Window>
        <Thread />
      </div>
    </GiphyContext.Provider>
  );
};

const TeamChannelHeader = ({setIsEditing}) => {
    const {channel, watcher_count} = useChannelStateContext();
    const {client} = useChatContext();
  
    const MessagingHeader = () => {
      const members = Object.values(channel.state.members).filter(({user}) => user.id !== client.userID);
      const additionalMembers = members.length - 3;
  
      if(channel.type === "messaging") {
        return (
          <div className = "teamChannelHeader">
            {members.map(({user}, i) => (
              <div key = {i} className = "teamChannelHeaderM">
                <Avatar image = {user.image} name = {user.username || "user.id"} size = {32} />
                <p className = "teamChannelHeaderName user">{user.username || user.id}</p>
              </div>
            ))}
  
            {additionalMembers > 0 && <p className = "teamChannelHeaderName user">and {additionalMembers} more</p>}
          </div>
        );
      }
  
      return (
        <div className = "teamChannelHeader2">
          <p className = "teamChannelHeaderName"># {channel.data.name}</p>
          <span style={{display: "flex"}} onClick = {() => setIsEditing(true)}>
            <ChannelInfo />
          </span>
        </div>
      );
    };
  
    const getWatcherText = (watchers) => {
      if (!watchers) return "No users online";
      if (watchers === 1) return "1 user online";
      return `${watchers} users online`;
    };
  
    return (
      <div className = "teamChannelHeaderCont">
        <MessagingHeader />
        <div className="teamChannelHeaderR">
          <p className="teamChannelHeaderRText">{getWatcherText(watcher_count)}</p>
        </div>
      </div>
    );
  };

  export default ChannelInner;
