import React, {useEffect, useState} from "react"
import {Avatar, useChatContext} from "stream-chat-react"
import {InviteIcon} from '../assets/inviteIcon.png'
import { initialState } from "stream-chat-react/dist/components/Channel/channelState"

const ListContainer = ({children}) => {
    return (
        <div className = "user-list_container">
            <div className = "user-list_header">
                <p>User</p>
                <p>Invite</p>
            </div>
            {children}
        </div>
    )
}
const UserItem = ({user}) => {
    return (
        <div className = "user-item_wrapper">
            <div classname = "user-item_name-wrapper">
                <Avatar image = {user.image} name = {user.username || user.id} size = {32}/>
            </div>
        </div>
    )
}

const UserList = () => {
    const {client} = useChatContext()
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [listEmpty, setListEmpty] = useState(false)
    useEffect(() => {
        const getUsers = async () => {
            if(loading) return;
            setLoading(true);
            try{
                const response = await client.queryUsers(
                    {id: {$ne: client.userID}},
                    {id: 1},
                    {limit: 8}
                )
                if(response.users.length){
                    setUsers(response.users)
                } else{

                }
            } catch (error){
                console.log(error)
            }
            setLoading(false)
        }
        if(client) getUsers()
    }, [])
    return (
        <ListContainer>
            {loading ? <div className = "user-list_message">
                loading users
            </div> : (
                users?.map((user, i) => (
                <UserItem index = {i} key = {user.id} user = {user} />
            ))
        )}
        </ListContainer>
    )
}
export default UserList
