import React, {useState} from 'react'
import {useChatContext} from 'stream-chat-react'
import {UserList} from "./"
import {CloseCreateChannel} from "../assets/CloseCreateChannel.js"

const ChannelNameInput = ({channelName = '', setChannelName}) => {
  const handleChange = (event) => {
    event.preventDefault();
    setChannelName(event.target.value);
  }
  return(
    <div className = "channel-name-inpout_wrapper">
      <p>Name</p>
      <input value = {channelName} onChange = {handleChange} placeholder = 'channle-name' />
      <p>Add members</p>
    </div>
  )
}

const CreateChannel = ({createType, setIsCreating}) => {
  const {client, setActiveChannel} = useChatContext();
  const [selectedUsers, setSelectedUsers] = useState([client.userID || ''])
  const [channelName, setChannelName] = useState('');
  return (
    <div className = "create-channel_container"> 
        <div className = "create-channel_header">
          <p>
            {createType === "team" ? "create a new channel" : "Send direct message"}
          </p>
          <CloseCreateChannel setIsCreating = {setIsCreating} />
        </div>
        {createType === "team" && <ChannelNameInput channelName = {channelName} setChannelName = {setChannelName} />}
      <UserList setSelectedUsers = {setSelectedUsers} />
    </div>
  )
}

export default CreateChannel
