import React, {useState} from "react"
import {useChatContext} from "stream-chat-react";
import {UserList} from './';
import { CloseCreateChannel } from "../assets/CloseCreateChannel";

const ChannelNameInput = ({channelName = '', setChannelName}) => {
  const handleChange = (event) => {
    event.preventDefault();
    setChannelName(event.target.value);
  }
  return(
    <div className = "channel-name-inpout_wrapper">
      <p>Name</p>
      <input value = {channelName} onChange = {handleChange} placeholder = 'channle-name' />
      <p>Add members</p>
    </div>
  )
}

const EditChannel = ({setIsEditing}) => {
  const {channel} = useChatContext();
  const [channelName, setChannelName] = useState(channel?.data?.name);
  const [selectedUsers, setSelectedUsers] = useState([]);
  return (
    <div className = "edit-channel_container">
      <div className = "edit-channel_header">
        <p>Edit Channel</p>
        <CloseCreateChannel setIsEditing = {setIsEditing} />
      </div>
      <ChannelNameInput channelName = {channelName} setChannelName = {setChannelName} />
      <UserList setSelectedUsers = {setSelectedUsers} />
      <div className = "edit-channel_button-wrapper">
        <p>Save Changes</p>
      </div>
    </div>
  )
}

export default EditChannel
